import argparse

from certifi import contents, where

parser = argparse.ArgumentParser()
parser.add_argument("-c", "--contents", action="store_true")
args = parser.parse_args()

if args.contents:
    print(contents())
else:
    print(where())
# format 메서드 사용
a = 'Hello {2} {0} {2}'.format('1','2','3')
print(a)

b = 'Hello {} {} {}'.format('1','2','3')
print(b)

